package op.gg.joinus.main

import op.gg.joinus.model.RoomInfo
import java.util.*

data class HomeRoomListItem (
    var room: RoomInfo
){
    lateinit var title:String
    lateinit var idNTime:String
    lateinit var matchTime:String
    lateinit var numPeople:String
    lateinit var imgGame:String
    init{
        with(this){
            // set title
            title = room.room_name
            //set idNTime
            idNTime = ""
            for (n in room.user_list){
                if (n.pk == room.leader_pk){
                    idNTime += n.nickname
                }
            }
            // 생성날짜 추가
            idNTime += " | " + "room.date"
            //set matchTime
            val calendar = Calendar.getInstance()
            val currentDay = calendar.get(Calendar.DAY_OF_MONTH)
            val day = room.start_date.day
            matchTime = (day - currentDay).toString() + "일 뒤"
            if (day - currentDay <= 0){
                matchTime = "오늘 시작"
            }
            if (room.is_start == 1){
                matchTime = "매칭 완료"
            }
            //set numPeople
            numPeople =  room.now_people_cnt.toString() + "/" + room.people_number.toString()
            //set imgGame
            when(room.game_name){
                "lol"->{
                    imgGame = "data:image/jpeg;base64,"
                }
            }
        }
    }
}